mapper.select:
SELECT name, contact1, contact2, business_number
FROM supply
WHERE id = #{id};

SELECT name, contact1, contact2, business_number
FROM supply
order by id limit #{limit} offset #{offset};

mapper.update:
update supply
set name = #{name},
contact1 = #{contact1},
contact2 = #{contact2},
business_number = #{business_number}
where id = #{id};
